mapper.select:
SELECT u.LOGIN_NAME, u.DISPLAY_NAME, u.GROUP_ID, u.CREATE_ON, u.LAST_MODIFIED, u.DEALER_ID, u.SALES_ORG, u.LAST_LOGIN FROM t_sys_user u WHERE u.LOGIN_NAME in
#{loginName,jdbcType=VARCHAR}
order by u.CREATE_ON asc

select

from t_sys_user
where LOGIN_NAME = #{loginName,jdbcType=VARCHAR} and PASSWORD = #{password,jdbcType=VARCHAR}

SELECT DISTINCT u.LOGIN_NAME, u.DISPLAY_NAME, u.SMART_IDCARDNUMBER, u.MOBILE, u.CUSTOMIZED_EMPTYPETXT, u.DEPARTMENT_NAME, u.BRANCH_NO, u.DEALER_ID, u.SALES_ORG,de.DEALER_NAME,b.BRANCH_NAME,it.ITEM_NAME SALE_NAME, CASE WHEN r.ID IS NULL THEN false ELSE true end isRole, (select group_concat(r.ID) from t_sys_user_role r where r.LOGIN_NAME = u.LOGIN_NAME ) as roleIds FROM T_SYS_USER u JOIN T_SYS_USER_ROLE r ON u.LOGIN_NAME = r.LOGIN_NAME LEFT JOIN T_MD_BRANCH b on b.BRANCH_NO = u.BRANCH_NO LEFT JOIN T_MD_DEALER de on de.DEALER_NO = u.DEALER_ID LEFT JOIN T_SYS_CODE_ITEM it on it.ITEM_CODE = u.SALES_ORG and it.TYPE_CODE='1002'
r.ID = #{roleId,jdbcType=VARCHAR}
and (u.LOGIN_NAME like CONCAT('%',#{uname,jdbcType=VARCHAR},'%') or u.DISPLAY_NAME like CONCAT('%',#{uname,jdbcType=VARCHAR},'%') OR u.SMART_IDCARDNUMBER like CONCAT('%',#{uname,jdbcType=VARCHAR},'%') OR u.MOBILE like CONCAT('%',#{uname,jdbcType=VARCHAR},'%'))
and u.SALES_ORG = #{salesOrg,jdbcType=VARCHAR} or (u.CUSTOMIZED_HRREGION=#{cmpCode,jdbcType=VARCHAR} and (u.SALES_ORG is null or u.SALES_ORG=''))
and u.CUSTOMIZED_HRREGION=#{cmpCode,jdbcType=VARCHAR}
and u.SALES_ORG = #{salesOrg,jdbcType=VARCHAR}
order by u.CREATE_ON desc,u.LAST_MODIFIED desc

SELECT u.LOGIN_NAME, u.BRANCH_NO, u.SALES_ORG, u.DEALER_ID FROM t_sys_user u, t_sys_user_role r WHERE u.LOGIN_NAME = r.LOGIN_NAME
AND r.ID = #{rid}
AND u.SALES_ORG = #{salesOrg}
AND u.DEALER_ID = #{dealerNo}
AND u.BRANCH_NO = #{branchNo}

SELECT distinct u.LOGIN_NAME, u.BRANCH_NO, u.SALES_ORG, u.DEALER_ID, u.DISPLAY_NAME FROM t_sys_user u, t_sys_user_role r, t_md_branch b WHERE u.LOGIN_NAME = r.LOGIN_NAME AND u.BRANCH_NO = b.BRANCH_NO
AND b.DEALER_NO NOT IN (${dealerNo})
AND b.BRANCH_GROUP = #{branchGroup}
AND b.SALES_ORG = #{salesOrg} AND u.SALES_ORG = #{salesOrg}
AND r.ID = #{rid}

SELECT distinct u.LOGIN_NAME, u.BRANCH_NO, u.SALES_ORG, u.DEALER_ID, u.DISPLAY_NAME FROM t_sys_user u, t_sys_user_role r, t_md_branch b WHERE u.LOGIN_NAME = r.LOGIN_NAME
AND u.DEALER_ID = b.DEALER_NO AND b.DEALER_NO = #{dealerNo}
AND u.BRANCH_NO = b.BRANCH_NO AND u.BRANCH_NO = #{branchNo} AND b.BRANCH_NO = #{branchNo}
AND u.SALES_ORG = b.SALES_ORG AND b.SALES_ORG = #{salesOrg} AND u.SALES_ORG = #{salesOrg}
AND r.ID in (${rid})

SELECT u.LOGIN_NAME, u.DISPLAY_NAME, u.SMART_IDCARDNUMBER, u.MOBILE, u.CUSTOMIZED_EMPTYPETXT, u.DEPARTMENT_NAME, u.BRANCH_NO, u.DEALER_ID, u.SALES_ORG FROM t_sys_user u
NOT EXISTS (SELECT 1 FROM t_sys_user_role r WHERE u.LOGIN_NAME = r.LOGIN_NAME )
and (u.LOGIN_NAME like CONCAT('%',#{uname,jdbcType=VARCHAR},'%') or u.DISPLAY_NAME like CONCAT('%',#{uname,jdbcType=VARCHAR},'%') OR u.SMART_IDCARDNUMBER like CONCAT('%',#{uname,jdbcType=VARCHAR},'%') OR u.MOBILE like CONCAT('%',#{uname,jdbcType=VARCHAR},'%'))
and u.SALES_ORG = #{salesOrg,jdbcType=VARCHAR}
and u.CUSTOMIZED_HRREGION = #{companyCode,jdbcType=VARCHAR}

mapper.update:
update t_sys_user
set password = #{newPassword,jdbcType=VARCHAR}
where LOGIN_NAME = #{loginName,jdbcType=VARCHAR}
