mapper.select:
SELECT p.MATNR, p.MATNR_TXT, p.BASE_UNIT, p. STATUS, ifnull(p.SORT,0) SORT, p.LAST_MODIFIED MARA_LAST_MODIFIED, p.CREATE_AT MARA_CREATE_AT, p.FIRST_CAT, (SELECT i.ITEM_NAME FROM t_sys_code_item I WHERE i.TYPE_CODE= AND i.ITEM_CODE=p.FIRST_CAT) FIRST_CAT_NAME, p.SEC_CATE, (SELECT i.ITEM_NAME FROM t_sys_code_item I WHERE i.TYPE_CODE= AND i.ITEM_CODE=p.SEC_CATE) SEC_CATE_NAME, p.BRAND, (SELECT i.ITEM_NAME FROM t_sys_code_item I WHERE i.TYPE_CODE= AND i.ITEM_CODE=p.BRAND) BRAND_NAME, p.WEIGHT, p.WEIGHT_UNIT, p.IMPORTANT_PRD_FLAG, p.SPEC, (SELECT i.ITEM_NAME FROM t_sys_code_item I WHERE i.TYPE_CODE= AND i.ITEM_CODE=p.SPEC) SPEC_NAME, p.SPEC_UNIT, p.BOT_SPEC, p.ZBOT_CODE, (SELECT i.ITEM_NAME FROM t_sys_code_item I WHERE i.TYPE_CODE= AND i.ITEM_CODE=p.ZBOT_CODE) ZBOT_CODE_NAME, p.SALES_ORG, p.DEALER_NO, pex.rate, pex.SHORT_TXT, pex.PRE_DAYS, pex.BOT_TYPE, pex.MATNR EX_MATNR, pex.SALES_FLAG, pex.RET_BOT_FLAG, pex.CREATE_AT, pex.CREATE_BY, pex.CREATE_BY_TXT, pex.LAST_MODIFIED, pex.LAST_MODIFIED_BY, pex.LAST_MODIFIED_BY_TXT FROM t_md_mara p LEFT JOIN t_md_mara_ex pex ON (p.MATNR = pex.MATNR and p.SALES_ORG=pex.SALES_ORG)
p.FIRST_CAT = #{firstCatory,jdbcType=VARCHAR}
AND p.SEC_CATE = #{secCatory,jdbcType=VARCHAR}
AND IFNULL(p.STATUS,'N') = #{status,jdbcType=VARCHAR}
AND (p.MATNR like '%${matnrTxt}%' or p.MATNR_TXT like '%${matnrTxt}%' or pex.SHORT_TXT like '%${matnrTxt}%')
AND p.SALES_ORG=#{salesOrg}
AND p.DEALER_NO=#{dealerNo}
AND p.HIDE=#{hide}
ORDER BY p.SORT desc

SELECT p.MATNR, p.MATNR_TXT, p.BASE_UNIT, p. STATUS, p.LAST_MODIFIED MARA_LAST_MODIFIED, p.CREATE_AT MARA_CREATE_AT, p.FIRST_CAT, p.SEC_CATE, p.BRAND, p.WEIGHT, p.WEIGHT_UNIT, p.IMPORTANT_PRD_FLAG, p.SPEC, p.SPEC_UNIT, p.BOT_SPEC, p.ZBOT_CODE, p.SALES_ORG, p.DEALER_NO, pex.rate, pex.SHORT_TXT, pex.PRE_DAYS, pex.BOT_TYPE, pex.MATNR EX_MATNR, pex.SALES_FLAG, pex.RET_BOT_FLAG, pex.CREATE_AT, pex.CREATE_BY, pex.CREATE_BY_TXT, pex.LAST_MODIFIED, pex.LAST_MODIFIED_BY, pex.LAST_MODIFIED_BY_TXT FROM t_md_mara p LEFT JOIN t_md_mara_ex pex ON (p.MATNR = pex.MATNR and p.SALES_ORG=pex.SALES_ORG)
p.MATNR like #{productCode}
and p.SALES_ORG=#{salesOrg}

SELECT p.MATNR, p.MATNR_TXT, p.BASE_UNIT, p. STATUS, p.LAST_MODIFIED MARA_LAST_MODIFIED, p.CREATE_AT MARA_CREATE_AT, p.FIRST_CAT, p.SEC_CATE, p.BRAND, p.WEIGHT, p.WEIGHT_UNIT, p.IMPORTANT_PRD_FLAG, p.SPEC, p.SPEC_UNIT, p.BOT_SPEC, p.ZBOT_CODE, p.SALES_ORG, p.DEALER_NO FROM t_md_mara p
p. STATUS='Y'
AND p.SALES_ORG=#{salesOrg}
AND p.DEALER_NO=#{dealerNo}
AND NOT EXISTS (select * from T_MARA_PRICE_REL pl where pl.MATNR=p.MATNR and pl.id=#{id})
