mapper.select:
select

from t_vip_cust_info 


        MP = #{phone} 


        and SALES_ORG=#{salesOrg}

select (SELECT a.ACCT_AMT from t_vip_acct a WHERE i.VIP_CUST_NO = a.VIP_CUST_NO) ACCT_AMT ,i.VIP_CUST_NO, ( SELECT it.ITEM_NAME FROM T_SYS_CODE_ITEM it WHERE it.TYPE_CODE = AND it.ITEM_CODE = i.SALES_ORG ) SALES_ORG, i.VIP_NAME, i.ADDRESS_TXT, ( SELECT it.ITEM_NAME FROM T_SYS_CODE_ITEM it WHERE it.TYPE_CODE = AND it.ITEM_CODE = i.PROVINCE ) PROVINCE, ( SELECT it.ITEM_NAME FROM T_SYS_CODE_ITEM it WHERE it.TYPE_CODE = AND it.ITEM_CODE = i.CITY ) CITY, ( SELECT it.ITEM_NAME FROM T_SYS_CODE_ITEM it WHERE it.TYPE_CODE = AND it.ITEM_CODE = i.COUNTY ) COUNTY, i.STREET, i.ROAD, i.SUBDIST, a.RESIDENTIAL_AREA_TXT SUBDIST_NAME, i.ROOM, i.MP, i.VIP_MP, i.ZIP, i.Email, i.SEX, date_format(i.BIRTHDAY, '%Y-%m-%d') BIRTHDAY, i.CERT_ID, i.VIP_SRC, ( SELECT it.ITEM_NAME FROM T_SYS_CODE_ITEM it WHERE it.TYPE_CODE = AND it.ITEM_CODE = i.VIP_SRC ) VIP_SRC_NAME, i.MILKBOX, i.VIP_TYPE, ( SELECT it.ITEM_NAME FROM T_SYS_CODE_ITEM it WHERE it.TYPE_CODE = AND it.ITEM_CODE = i.VIP_TYPE ) VIP_TYPE_NAME, date_format(i.SUBSCRIBE_DATE, '%Y-%m-%d') SUBSCRIBE_DATE, i.SUBSCRIBER, i.ACTIVITY_NO, i. STATUS, i.VIP_AMT, i.CREATE_AT, i.CREATE_BY, date_format(i.FIRST_ORDER_TIME, '%Y-%m-%d') FIRST_ORDER_TIME, date_format(i.LAST_ORDER_TIME, '%Y-%m-%d') LAST_ORDER_TIME, i.CREATE_BY_TXT, i.LAST_MODIFIED, i.LAST_MODIFIED_BY, i.LAST_MODIFIED_BY_TXT, i.MEMO_TXT, i.DISP_MEMO_TXT, b.BRANCH_NAME,i.BRANCH_NO,i.DEALER_NO, ( SELECT t.ORG_CODE FROM t_md_org_cust oc LEFT JOIN t_md_order_org t on oc.ORG_ID = t.ID WHERE oc.CUST_ID = i.VIP_CUST_NO ) ORG_ID, ( SELECT og.ORG_NAME FROM t_md_org_cust oc LEFT JOIN t_md_order_org og on oc.ORG_ID = og.ID WHERE oc.CUST_ID = i.VIP_CUST_NO ) ORG_NAME, (CASE i.DEALER_NO WHEN '-1' THEN '' ELSE (SELECT d.dealer_name from t_md_dealer d where d.dealer_no=i.DEALER_NO) END) DEALER_NAME from t_vip_cust_info i LEFT JOIN T_MD_BRANCH b ON b.BRANCH_NO = i.BRANCH_NO LEFT JOIN t_md_residential_area a ON a.ID=i.SUBDIST where 1=1
AND i.SALES_ORG = #{salesOrg}
and b.BRANCH_GROUP=#{stationType} and i.DEALER_NO = #{dealerNo} and i.BRANCH_NO = #{station}
and b.BRANCH_GROUP=#{stationType} and i.DEALER_NO = #{dealerNo}
and b.BRANCH_GROUP=#{stationType} and i.DEALER_NO = #{station}
and b.BRANCH_GROUP=#{stationType}
and b.BRANCH_GROUP=#{stationType} and i.BRANCH_NO = #{station}
and b.BRANCH_GROUP=#{stationType}
and i.BRANCH_NO = #{station}
and i.STATUS = #{status}
and i.SUBSCRIBE_DATE >= #{startDate}
and i.SUBSCRIBE_DATE <= #{endDate}
and (i.VIP_NAME like '%${content}%' or i.ADDRESS_TXT like '%${content}%' or i.MP like '%${content}%' or a.RESIDENTIAL_AREA_TXT like '%${content}%' or concat(a.RESIDENTIAL_AREA_TXT,i.ADDRESS_TXT) like '%${content}%')
and i.MP = #{phone}
order by i.CREATE_AT desc,i.LAST_MODIFIED desc,i.BRANCH_NO,i.DEALER_NO

select (SELECT a.ACCT_AMT from t_vip_acct a WHERE i.VIP_CUST_NO = a.VIP_CUST_NO) ACCT_AMT ,i.VIP_CUST_NO, ( SELECT it.ITEM_NAME FROM T_SYS_CODE_ITEM it WHERE it.TYPE_CODE = AND it.ITEM_CODE = i.SALES_ORG ) SALES_ORG, i.VIP_NAME, i.ADDRESS_TXT, ( SELECT it.ITEM_NAME FROM T_SYS_CODE_ITEM it WHERE it.TYPE_CODE = AND it.ITEM_CODE = i.PROVINCE ) PROVINCE, ( SELECT it.ITEM_NAME FROM T_SYS_CODE_ITEM it WHERE it.TYPE_CODE = AND it.ITEM_CODE = i.CITY ) CITY, ( SELECT it.ITEM_NAME FROM T_SYS_CODE_ITEM it WHERE it.TYPE_CODE = AND it.ITEM_CODE = i.COUNTY ) COUNTY, i.STREET, i.ROAD, i.SUBDIST, a.RESIDENTIAL_AREA_TXT SUBDIST_NAME, i.ROOM, i.MP, i.VIP_MP, i.ZIP, i.Email, i.SEX, date_format(i.BIRTHDAY, '%Y-%m-%d') BIRTHDAY, i.CERT_ID, i.VIP_SRC, ( SELECT it.ITEM_NAME FROM T_SYS_CODE_ITEM it WHERE it.TYPE_CODE = AND it.ITEM_CODE = i.VIP_SRC ) VIP_SRC_NAME, i.MILKBOX, i.VIP_TYPE, ( SELECT it.ITEM_NAME FROM T_SYS_CODE_ITEM it WHERE it.TYPE_CODE = AND it.ITEM_CODE = i.VIP_TYPE ) VIP_TYPE_NAME, date_format(i.SUBSCRIBE_DATE, '%Y-%m-%d') SUBSCRIBE_DATE, i.SUBSCRIBER, i.ACTIVITY_NO, i. STATUS, i.VIP_AMT, i.CREATE_AT, i.CREATE_BY, date_format(i.FIRST_ORDER_TIME, '%Y-%m-%d') FIRST_ORDER_TIME, date_format(i.LAST_ORDER_TIME, '%Y-%m-%d') LAST_ORDER_TIME, i.CREATE_BY_TXT, i.LAST_MODIFIED, i.LAST_MODIFIED_BY, i.LAST_MODIFIED_BY_TXT, i.MEMO_TXT, i.DISP_MEMO_TXT, b.BRANCH_NAME,i.BRANCH_NO,i.DEALER_NO,(CASE i.DEALER_NO WHEN '-1' THEN '' ELSE (SELECT d.dealer_name from t_md_dealer d where d.dealer_no=i.DEALER_NO) END) DEALER_NAME from t_vip_cust_info i INNER JOIN t_md_org_cust oct on oct.CUST_ID = i.VIP_CUST_NO and oct.ORG_ID = #{orgId,jdbcType=VARCHAR} LEFT JOIN T_MD_BRANCH b ON b.BRANCH_NO = i.BRANCH_NO LEFT JOIN t_md_residential_area a ON a.ID=i.SUBDIST where 1=1
AND i.SALES_ORG = #{salesOrg}
and b.BRANCH_GROUP=#{stationType} and i.DEALER_NO = #{dealerNo} and i.BRANCH_NO = #{station}
and b.BRANCH_GROUP=#{stationType} and i.DEALER_NO = #{dealerNo}
and b.BRANCH_GROUP=#{stationType}
and b.BRANCH_GROUP=#{stationType} and i.BRANCH_NO = #{station}
and b.BRANCH_GROUP=#{stationType}
and i.BRANCH_NO = #{station}
and i.STATUS = #{status}
and i.SUBSCRIBE_DATE >= #{startDate}
and i.SUBSCRIBE_DATE <= #{endDate}
and (i.VIP_NAME like '%${content}%' or i.ADDRESS_TXT like '%${content}%' or i.MP like '%${content}%' or a.RESIDENTIAL_AREA_TXT like '%${content}%' or concat(a.RESIDENTIAL_AREA_TXT,i.ADDRESS_TXT) like '%${content}%')
and i.MP = #{phone}
order by i.CREATE_AT desc,i.LAST_MODIFIED desc,i.BRANCH_NO,i.DEALER_NO

 select count(i.VIP_CUST_NO)  from t_vip_cust_info i,T_MD_BRANCH b
	where b.BRANCH_NO = i.BRANCH_NO 

         AND i.SALES_ORG = #{salesOrg} 








							   and b.BRANCH_GROUP=#{stationType} and i.DEALER_NO = #{dealerNo} and i.BRANCH_NO = #{station}


