SELECT o.ORDER_NO, ( CASE WHEN o.DISCOUNT_AMT > 0 THEN CONCAT('折扣',o.DISCOUNT_AMT,'元') ELSE '' END ) PROM_DESCRIBE, o.BRANCH_NO, o.EMP_NO, e.MP EMP_TEL, e.EMP_NAME, c.VIP_NAME, c.MP CUST_TEL, o.INIT_AMT, bill.SUPP_AMT ORDER_AMT, bill.CUST_ACC_AMT, (SELECT MIN(START_DISP_DATE) FROM t_mst_plan_order_item WHERE ORDER_NO=o.ORDER_NO) START_DATE, o.END_DATE, concat( (SELECT it.ITEM_NAME FROM t_sys_code_item it WHERE it.TYPE_CODE = AND it.ITEM_CODE = a.PROVINCE), (SELECT it.ITEM_NAME FROM t_sys_code_item it WHERE it.TYPE_CODE = AND it.ITEM_CODE = a.CITY), (SELECT it.ITEM_NAME FROM t_sys_code_item it WHERE it.TYPE_CODE = AND it.ITEM_CODE = a.COUNTY), IFNULL( (SELECT ra.ADDRESS_TXT from t_md_residential_area ra WHERE ra.ID = a.RESIDENTIAL_AREA),''), IFNULL((SELECT ra.RESIDENTIAL_AREA_TXT from t_md_residential_area ra WHERE ra.ID = a.RESIDENTIAL_AREA),''), IFNULL(a.ADDRESS_TXT,'') )CUST_ADDRESS, concat(
IFNULL((SELECT ra.RESIDENTIAL_AREA_TXT from t_md_residential_area ra WHERE ra.ID = a.RESIDENTIAL_AREA),''), IFNULL(a.ADDRESS_TXT,'') )CUST_ADDRESS_SHORT ,i.MATNR,ex.SHORT_TXT MATNR_TXT,i.UNIT,sum(i.DISP_TOTAL) QTY,i.SALES_PRICE BASE_PRICE,(sum(i.DISP_TOTAL) * i.SALES_PRICE) TOTAL_PRICE from t_preorder o left JOIN t_promotion p on o.PROMOTION = p.PROM_NO LEFT JOIN t_mst_recv_bill bill on o.ORDER_NO = bill.ORDER_NO and bill.HAD_OFFSET = 'N' LEFT JOIN t_md_address a on o.ADRESS_NO = a.ADDRESS_ID left join t_md_residential_area r on r.ID = a.RESIDENTIAL_AREA LEFT JOIN t_md_branch_emp e on o.EMP_NO = e.EMP_NO LEFT JOIN t_vip_cust_info c on c.VIP_CUST_NO = o.MILKMEMBER_NO LEFT JOIN t_vip_acct acct on o.MILKMEMBER_NO =acct.VIP_CUST_NO LEFT JOIN t_mst_plan_order_item i on o.ORDER_NO = i.ORDER_NO LEFT JOIN t_md_mara_ex ex on i.MATNR = ex.MATNR and ex.SALES_ORG=#{salesOrg,jdbcType=VARCHAR} WHERE 1=1 and (o.PREORDER_SOURCE='20' OR o.PREORDER_SOURCE ='30' OR o.PREORDER_SOURCE ='50'OR o.PREORDER_SOURCE ='70' ) and (o.PREORDER_STAT = '10' OR o.PREORDER_STAT = '30') and ( p.PROM_SUB_TYPE!='Z017' OR p.PROM_NO='' OR p.PROM_NO is null) and o.INIT_AMT > 0 and o.PAYMENTMETHOD ='20'
and o.ORDER_NO = #{orderNo,jdbcType=VARCHAR}
and o.EMP_NO = #{empNo,jdbcType=VARCHAR}
and o.BRANCH_NO = #{branchNo,jdbcType=VARCHAR}
and o.PAYMENT_STAT = #{status,jdbcType=VARCHAR}
and o.END_DATE >= #{orderStartDate,jdbcType=DATE}
and o.END_DATE <= #{orderEndDate,jdbcType=DATE}
and ( o.ORDER_NO like '%${search}%' or e.EMP_NAME like'%${search}%' or c.VIP_NAME like '%${search}%' or c.MP like '%${search}%' or r.RESIDENTIAL_AREA_TXT like '%${search}%' or a.ADDRESS_TXT like '%${search}%' or CONCAT(r.RESIDENTIAL_AREA_TXT,a.ADDRESS_TXT) like '%${search}%' )
GROUP BY o.ORDER_NO,i.MATNR ORDER BY c.SUBDIST,c.ADDRESS_TXT,CUST_ADDRESS_SHORT

SELECT o.ORDER_NO, o.BRANCH_NO, o.EMP_NO, e.MP EMP_TEL, e.EMP_NAME, c.VIP_NAME, c.MP CUST_TEL, o.INIT_AMT, bill.SUPP_AMT ORDER_AMT, bill.CUST_ACC_AMT, o.END_DATE, (SELECT MIN(DISP_DATE) FROM t_mst_order_daliy_plan_item WHERE ORDER_NO=o.ORDER_NO) START_DATE, concat( (SELECT it.ITEM_NAME FROM t_sys_code_item it WHERE it.TYPE_CODE = AND it.ITEM_CODE = a.PROVINCE), (SELECT it.ITEM_NAME FROM t_sys_code_item it WHERE it.TYPE_CODE = AND it.ITEM_CODE = a.CITY), (SELECT it.ITEM_NAME FROM t_sys_code_item it WHERE it.TYPE_CODE = AND it.ITEM_CODE = a.COUNTY), IFNULL( (SELECT ra.ADDRESS_TXT from t_md_residential_area ra WHERE ra.ID = a.RESIDENTIAL_AREA),''), IFNULL((SELECT ra.RESIDENTIAL_AREA_TXT from t_md_residential_area ra WHERE ra.ID = a.RESIDENTIAL_AREA),''), IFNULL(a.ADDRESS_TXT,'') )CUST_ADDRESS, concat(
IFNULL((SELECT ra.RESIDENTIAL_AREA_TXT from t_md_residential_area ra WHERE ra.ID = a.RESIDENTIAL_AREA),''), IFNULL(a.ADDRESS_TXT,'') )CUST_ADDRESS_SHORT ,i.ORDER_NO,i.MATNR,ex.SHORT_TXT MATNR_TXT,i.UNIT,SUM(i.QTY) QTY,i.PRICE BASE_PRICE,sum(i.AMT) TOTAL_PRICE from t_preorder o LEFT JOIN t_mst_recv_bill bill on o.ORDER_NO = bill.ORDER_NO and bill.HAD_OFFSET = 'N' LEFT JOIN t_md_address a on o.ADRESS_NO = a.ADDRESS_ID left join t_md_residential_area r on r.ID = a.RESIDENTIAL_AREA LEFT JOIN t_md_branch_emp e on o.EMP_NO = e.EMP_NO LEFT JOIN t_vip_cust_info c on c.VIP_CUST_NO = o.MILKMEMBER_NO LEFT JOIN t_vip_acct acct on o.MILKMEMBER_NO =acct.VIP_CUST_NO LEFT JOIN t_mst_order_daliy_plan_item i on i.ORDER_NO = o.ORDER_NO AND (i.STATUS ='10' OR i.`STATUS` ='20') LEFT JOIN t_md_mara_ex ex on i.MATNR = ex.MATNR and ex.SALES_ORG = #{salesOrg,jdbcType=VARCHAR} WHERE 1=1 and o.INIT_AMT > 0 and (o.PREORDER_SOURCE='20' OR o.PREORDER_SOURCE ='30' OR o.PREORDER_SOURCE ='50'OR o.PREORDER_SOURCE ='70' ) and (o.PREORDER_STAT = '10' OR o.PREORDER_STAT = '30') and o.INIT_AMT > 0
and o.PAYMENTMETHOD ='10'
and o.ORDER_NO = #{orderNo,jdbcType=VARCHAR}
and o.EMP_NO = #{empNo,jdbcType=VARCHAR}
and o.BRANCH_NO = #{branchNo,jdbcType=VARCHAR}
and o.PAYMENT_STAT = #{status,jdbcType=VARCHAR}
and o.END_DATE >= #{orderStartDate,jdbcType=DATE}
and o.END_DATE <= #{orderEndDate,jdbcType=DATE}
and ( o.ORDER_NO like '%${search}%' or e.EMP_NAME like'%${search}%' or c.VIP_NAME like '%${search}%' or c.MP like '%${search}%' or r.RESIDENTIAL_AREA_TXT like '%${search}%' or a.ADDRESS_TXT like '%${search}%' or CONCAT(r.RESIDENTIAL_AREA_TXT,a.ADDRESS_TXT) like '%${search}%' )
GROUP BY o.ORDER_NO,i.MATNR ORDER BY c.SUBDIST,c.ADDRESS_TXT,CUST_ADDRESS_SHORT

SELECT o.BRANCH_NO, o.EMP_NO , sum(b.AMT) AMT, b.RECEIPT_DATE, b.HAD_OFFSET, sum(f.AMT) OFFSET_AMT FROM t_preorder o LEFT JOIN t_mst_recv_bill b on o.ORDER_NO = b.ORDER_NO LEFT JOIN t_mst_recv_offset f on b.RECEIPT_NO = f.RECEIPT_NO WHERE b.`STATUS`='20' and o.BRANCH_NO= #{branchNo,jdbcType=VARCHAR}
and b.RECEIPT_DATE = #{receiptDate,jdbcType=DATE}
GROUP BY b.RECEIPT_DATE,o.EMP_NO ORDER BY o.ORDER_NO,o.EMP_NO

SELECT o.BRANCH_NO, o.EMP_NO , sum(b.AMT) AMT, b.RECEIPT_DATE, b.HAD_OFFSET, sum(f.AMT) OFFSET_AMT FROM t_preorder o LEFT JOIN t_mst_recv_bill b on o.ORDER_NO = b.ORDER_NO LEFT JOIN t_mst_recv_offset f on b.RECEIPT_NO = f.RECEIPT_NO WHERE b.`STATUS`='20' and o.BRANCH_NO= #{branchNo,jdbcType=VARCHAR}
and o.EMP_NO = #{empNo,jdbcType=VARCHAR}
and b.RECEIPT_DATE = #{receiptDate,jdbcType=DATE}
GROUP BY b.RECEIPT_DATE,o.EMP_NO ORDER BY o.ORDER_NO,o.EMP_NO
