SELECT SUM(di.CONFIRM_QTY) QTY,di.CONFIRM_MATNR,p.PROMOTION PROMOTION_FLAG from t_mst_disp_order_item di LEFT JOIN t_mst_disp_order ti ON di.ORDER_NO = ti.ORDER_NO LEFT JOIN t_preorder o on di.ORG_ORDER_NO = o.ORDER_NO LEFT JOIN t_mst_plan_order_item p on di.ORG_ITEM_NO = p.ITEM_NO LEFT JOIN t_md_mara_ex e on di.MATNR = e.MATNR and e.SALES_ORG = #{salesOrg,jdbcType=VARCHAR} where 1=1 AND di.GIFT_FLAG is NOT null AND ti.STATUS = '20' and di.CONFIRM_QTY>0 and o.BRANCH_NO = #{branchNo,jdbcType=VARCHAR}
and o.PREORDER_SOURCE = '40'
and e.SALES_ORG = #{salesOrg,jdbcType=VARCHAR} and ti.DISP_DATE = #{orderDate,jdbcType=DATE} GROUP BY di.CONFIRM_MATNR,p.PROMOTION

