mapper.select:
select distinct p.PROM_SUB_TYPE, i.ITEM_NAME PROM_SUB_TYPE_NAME, pro1.SHORT_TXT ORG_MATNR, oi.ORIG_NUM ORG_QTY, pro2.SHORT_TXT GIFT_MATNR,gi.GIFT_NUM GIFT_QTY, oi.ITEM_NO, p.PROM_NO, (case when p.PROM_SUB_TYPE = 'Z008' then CONCAT('购买',ROUND(oi.ORIG_NUM),'个',a1.SHORT_TXT,'赠送',ROUND(gi.GIFT_NUM),'个',a2.SHORT_TXT,'产品') when p.PROM_SUB_TYPE = 'Z015' then CONCAT('购买',a1.SHORT_TXT,'满', ci.LOW, '元钱减',oi.DISCOUNT_AMT,'元钱') when p.PROM_SUB_TYPE = 'Z016' then CONCAT('整单满',ci.LOW,'元减',oi.DISCOUNT_AMT, '元') else p.PROM_DES END ) PROM_DES, p.PROM_TYPE, p.SALES_ORG, p.SALES_DIST, p.CUSTOMER_TYPE, p.PROM_STAT, p.PLAN_START_TIME, p.PLAN_STOP_TIME, p.BUY_START_TIME, p.BUY_STOP_TIME, p.BUY_AMT, p.GIFT_AMT, oi.DISCOUNT_AMT, ci.LOW from t_promotion p left join t_sys_code_item i on p.PROM_SUB_TYPE = i.ITEM_CODE and i.TYPE_CODE= left join t_promotion_orig_item oi on p.PROM_NO = oi.PROM_NO left join t_promotion_gift_item gi on p.PROM_NO = gi.PROM_NO and oi.item_no = gi.item_no LEFT JOIN t_promotion_con_item ci ON p.PROM_NO = ci.PROM_NO and ci.item_no = oi.item_no LEFT JOIN t_md_mara_ex a1 ON oi.GOOD_NO = a1.MATNR AND p.SALES_ORG = a1.SALES_ORG LEFT JOIN t_md_mara_ex a2 ON gi.GIFT_NO = a2.MATNR AND p.SALES_ORG = a2.SALES_ORG left join t_prom_scope_item si on p.PROM_NO = si.PROM_NO left join t_md_mara_ex pro1 on pro1.matnr = oi.GOOD_NO left join t_md_mara_ex pro2 on pro2.matnr = gi.GIFT_NO where 1 = 1 and p.SALES_ORG = #{salesOrg,jdbcType=VARCHAR}
AND si.BRANCH_NO = #{branchNo,jdbcType=VARCHAR}
AND p.PLAN_START_TIME >= #{orderDateStart}
AND p.PLAN_STOP_TIME <= #{orderDateEnd}
order by p.PROM_NO desc

select o.PROMOTION PROM_NO,o.BRANCH_NO,count(1) NUMBER from t_preorder o,t_promotion p where o.PROMOTION = p.PROM_NO and o.PROMOTION = #{promNo} and o.BRANCH_NO in
#{branchNos}
GROUP BY o.PROMOTION,o.BRANCH_NO HAVING count(1)>0

select distinct p.PROM_NO, p.PROM_DES, p.PROM_SUB_TYPE, i.ITEM_NAME PROM_SUB_TYPE_NAME, p.SALES_ORG, p.SALES_DIST, p.CUSTOMER_TYPE, p.PROM_STAT, p.PLAN_START_TIME, p.PLAN_STOP_TIME, p.BUY_START_TIME, p.BUY_STOP_TIME from t_promotion p left join t_sys_code_item i on p.PROM_SUB_TYPE = i.ITEM_CODE and i.TYPE_CODE= left join t_prom_scope_item si on p.PROM_NO = si.PROM_NO where 1 = 1 and p.SALES_ORG = #{salesOrg,jdbcType=VARCHAR}
AND si.BRANCH_NO = #{branchNo,jdbcType=VARCHAR}
AND p.PLAN_START_TIME >= #{orderDateStart}
AND p.PLAN_STOP_TIME <= #{orderDateEnd}
AND p.PROM_NO like '%${promNo}%'
order by p.PROM_NO desc

         select distinct
			p.PROM_SUB_TYPE, oi.ORIG_NUM ORG_QTY,
	        p.PROM_NO, p.PROM_DES, p.PROM_TYPE,
            p.SALES_ORG, p.SALES_DIST, p.CUSTOMER_TYPE,
            p.PROM_STAT, p.PLAN_START_TIME, p.PLAN_STOP_TIME,
            p.BUY_START_TIME, p.BUY_STOP_TIME,oi.DISCOUNT_AMT,ci.LOW LOW_AMT,oi.ITEM_NO
		from t_promotion p
		left join t_promotion_orig_item oi on p.PROM_NO = oi.PROM_NO
		left join t_promotion_con_item ci on ci.PROM_NO = oi.PROM_NO and ci.ITEM_NO = oi.ITEM_NO
		left join t_prom_scope_item si on p.PROM_NO = si.PROM_NO
		where 1 = 1
		and p.PROM_SUB_TYPE = 'Z017'
		and p.PLAN_START_TIME <= #{createBy,jdbcType=DATE}
		and p.PLAN_STOP_TIME >= #{createBy,jdbcType=DATE}
		and p.BUY_START_TIME <= #{buyStartTime,jdbcType=DATE}
        and p.BUY_STOP_TIME >= #{buyStopTime,jdbcType=DATE}
		and p.SALES_ORG = #{salesOrg,jdbcType=VARCHAR}
		and si.branch_no = #{branchNo,jdbcType=VARCHAR}

select distinct
p.PROM_SUB_TYPE, oi.ORIG_NUM ORG_QTY,
p.PROM_NO, p.PROM_DES, p.PROM_TYPE,
p.SALES_ORG, p.SALES_DIST, p.CUSTOMER_TYPE,
p.PROM_STAT, p.PLAN_START_TIME, p.PLAN_STOP_TIME,
p.BUY_START_TIME, p.BUY_STOP_TIME,oi.DISCOUNT_AMT,ci.LOW LOW_AMT,oi.ITEM_NO
from t_promotion p
left join t_promotion_orig_item oi on p.PROM_NO = oi.PROM_NO
left join t_promotion_con_item ci on ci.PROM_NO = oi.PROM_NO and ci.ITEM_NO = oi.ITEM_NO
left join t_prom_scope_item si on p.PROM_NO = si.PROM_NO
where 1 = 1
and p.PROM_SUB_TYPE = 'Z016'
and ci.LOW <= #{buyAmt,jdbcType=DECIMAL}
and p.PLAN_START_TIME <= #{createBy,jdbcType=DATE}
and p.PLAN_STOP_TIME >= #{createBy,jdbcType=DATE}
and p.BUY_START_TIME <= #{buyStartTime,jdbcType=DATE}
and p.BUY_STOP_TIME >= #{buyStopTime,jdbcType=DATE}
and p.SALES_ORG = #{salesOrg,jdbcType=VARCHAR}
and si.branch_no = #{branchNo,jdbcType=VARCHAR}

select distinct
pro1.MATNR_TXT ORG_MATNR ,
p.PROM_SUB_TYPE, oi.ORIG_NUM ORG_QTY,
p.PROM_NO, p.PROM_DES, p.PROM_TYPE,
p.SALES_ORG, p.SALES_DIST, p.CUSTOMER_TYPE,
p.PROM_STAT, p.PLAN_START_TIME, p.PLAN_STOP_TIME,
p.BUY_START_TIME, p.BUY_STOP_TIME,oi.DISCOUNT_AMT,ci.LOW LOW_AMT,oi.ITEM_NO
from t_promotion p
left join t_promotion_orig_item oi on p.PROM_NO = oi.PROM_NO
left join t_promotion_con_item ci on ci.PROM_NO = oi.PROM_NO and ci.ITEM_NO = oi.ITEM_NO
left join t_prom_scope_item si on p.PROM_NO = si.PROM_NO
left join t_md_mara pro1 on pro1.matnr = oi.GOOD_NO
where 1 = 1
and p.PROM_SUB_TYPE = 'Z015'
and oi.GOOD_NO = #{orgMatnr,jdbcType=VARCHAR}
and ci.LOW <= #{buyAmt,jdbcType=DECIMAL}
and p.PLAN_START_TIME <= #{createBy,jdbcType=DATE}
and p.PLAN_STOP_TIME >= #{createBy,jdbcType=DATE}
and p.BUY_START_TIME <= #{buyStartTime,jdbcType=DATE}
and p.BUY_STOP_TIME >= #{buyStopTime,jdbcType=DATE}
and p.SALES_ORG = #{salesOrg,jdbcType=VARCHAR}
and si.branch_no = #{branchNo,jdbcType=VARCHAR}

    select
    p.PROM_NO,oi.ITEM_NO PROM_ITEM_NO,p.PROM_SUB_TYPE,(select NULL from DUAL) DISCOUNT_AMT
    ,
    CONCAT(
    '购买',
    ROUND(oi.ORIG_NUM),
    '个',
    a1.SHORT_TXT,
    '赠送',
    ROUND(gi.GIFT_NUM),
    '个',
    a2.SHORT_TXT,
    '产品'
    ) ITEM_DESCRIBE
    from t_promotion p
    left join t_promotion_orig_item oi on p.PROM_NO = oi.PROM_NO
    left join t_promotion_con_item ci on p.PROM_NO = ci.PROM_NO and  ci.ITEM_NO = oi.ITEM_NO
    left join t_promotion_gift_item gi on p.PROM_NO = gi.PROM_NO
    left join t_prom_scope_item si on p.PROM_NO = si.PROM_NO
    LEFT JOIN t_md_mara_ex a1 ON oi.GOOD_NO = a1.MATNR AND p.SALES_ORG = a1.SALES_ORG
    LEFT JOIN t_md_mara_ex a2 ON gi.GIFT_NO = a2.MATNR AND p.SALES_ORG = a2.SALES_ORG
    where 1 = 1
    and si.BRANCH_NO = #{branchNo,jdbcType=VARCHAR}
    and oi.GOOD_NO = #{orgMatnr,jdbcType=VARCHAR}
    and p.SALES_ORG =  #{salesOrg,jdbcType=VARCHAR}
    and p.PLAN_START_TIME <=#{planStartTime,jdbcType=DATE}
    and p.PLAN_STOP_TIME >=#{planStopTime,jdbcType=DATE}
    and p.BUY_START_TIME <=#{buyStartTime,jdbcType=DATE}
    and p.BUY_STOP_TIME >=#{buyStopTime,jdbcType=DATE}
    and  oi.ORIG_NUM <= #{orgQty,jdbcType=DECIMAL}
    and p.PROM_SUB_TYPE ='Z008'
UNION ALL
    select
    p.PROM_NO,oi.ITEM_NO PROM_ITEM_NO,p.PROM_SUB_TYPE,oi.DISCOUNT_AMT,
    CONCAT(
    a.SHORT_TXT,
    '满',
    ci.LOW,
    '元钱减',
    oi.DISCOUNT_AMT,
    '元钱'
    ) ITEM_DESCRIBE
    from t_promotion p
    left join t_promotion_orig_item oi on p.PROM_NO = oi.PROM_NO
    left join t_promotion_con_item ci on p.PROM_NO = ci.PROM_NO  and ci.ITEM_NO = oi.ITEM_NO
    left join t_promotion_gift_item gi on p.PROM_NO = gi.PROM_NO
    left join t_prom_scope_item si on p.PROM_NO = si.PROM_NO
    LEFT JOIN t_md_mara_ex a on oi.GOOD_NO = a.MATNR  and p.SALES_ORG = a.SALES_ORG
    where 1 = 1
    and si.BRANCH_NO = #{branchNo,jdbcType=VARCHAR}
    and oi.GOOD_NO = #{orgMatnr,jdbcType=VARCHAR}
    and p.SALES_ORG =  #{salesOrg,jdbcType=VARCHAR}
    and p.PLAN_START_TIME <=#{planStartTime,jdbcType=DATE}
    and p.PLAN_STOP_TIME >=#{planStopTime,jdbcType=DATE}
    and p.BUY_START_TIME <=#{buyStartTime,jdbcType=DATE}
    and p.BUY_STOP_TIME >=#{buyStopTime,jdbcType=DATE}
    and ci.LOW <=#{lowAmt,jdbcType=DECIMAL}
    and p.PROM_SUB_TYPE ='Z015'

SELECT DISTINCT
       p.PROM_NO,
       oi.ITEM_NO PROM_ITEM_NO,
       p.PROM_SUB_TYPE,
       oi.DISCOUNT_AMT,
       CONCAT(
           '整单满',
           ci.LOW,
           '元减',
           oi.DISCOUNT_AMT,
           '元'
       ) ITEM_DESCRIBE
   FROM
       t_promotion p
   LEFT JOIN t_promotion_orig_item oi ON p.PROM_NO = oi.PROM_NO
   LEFT JOIN t_promotion_con_item ci ON ci.PROM_NO = oi.PROM_NO  AND ci.ITEM_NO = oi.ITEM_NO
   LEFT JOIN t_prom_scope_item si ON p.PROM_NO = si.PROM_NO
   where 1 = 1
   and p.PROM_SUB_TYPE = 'Z016'
   and ci.LOW <= #{lowAmt,jdbcType=DECIMAL}
   and p.PLAN_START_TIME <= #{planStartTime,jdbcType=DATE}
   and p.PLAN_STOP_TIME >= #{planStopTime,jdbcType=DATE}
   and p.BUY_START_TIME <= #{buyStartTime,jdbcType=DATE}
   and p.BUY_STOP_TIME >= #{buyStopTime,jdbcType=DATE}
   and p.SALES_ORG = #{salesOrg,jdbcType=VARCHAR}
   and si.BRANCH_NO = #{branchNo,jdbcType=VARCHAR}
